internal import Combineimport CoreLocationimport SwiftUIstruct GeoResult: Equatable {    let lat: String    let lng: String}class GeocodingManager: ObservableObject {    @Published var result: GeoResult?    @Published var isLoading: Bool = false    private let geocoder = CLGeocoder()    func fetchCoordinates(street: String,                          suite: String,                          city: String,                          zipcode: String) {        let address = [street, suite, city, zipcode].joined(separator: ", ")        isLoading = true        result = nil        geocoder.geocodeAddressString(address) { placemarks, error in            DispatchQueue.main.async {                self.isLoading = false                if let error = error {                    print(error)                    return                }                guard let location = placemarks?.first?.location else {                    print("No coordinates found for this address")                    return                }                let coord = location.coordinate                self.result = GeoResult(                    lat: String(coord.latitude),                    lng: String(coord.longitude)                )            }        }    }}